CREATE TABLE "Dificultad" (
	"idDificultad" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"nombreDificultad" INTEGER NOT NULL,
	PRIMARY KEY("idDificultad")
);

CREATE TABLE "Marca" (
	"idMarca" INTEGER NOT NULL UNIQUE,
	"nombreMarca" VARCHAR NOT NULL,
	PRIMARY KEY("idMarca")
);

CREATE TABLE "ProcesoPlanta" (
	"idProcesoPlanta" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"plantaColor" VARCHAR NOT NULL,
	"idProceso" INTEGER NOT NULL,
	PRIMARY KEY("idProcesoPlanta")
);

CREATE TABLE "Procesos" (
	"idProcesos" INTEGER NOT NULL UNIQUE,
	"nombreProceso" VARCHAR NOT NULL,
	"idDificultad" INTEGER NOT NULL,
	PRIMARY KEY("idProcesos")
);

CREATE TABLE "Plantas" (
	"colorPlanta" VARCHAR NOT NULL UNIQUE,
	"nombrePlanta" VARCHAR NOT NULL,
	"superficie" DECIMAL NOT NULL,
	PRIMARY KEY("colorPlanta")
);

CREATE TABLE "Tecnico" (
	"dni" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"nombreTecnico" VARCHAR NOT NULL,
	"apellidoTecnico1" VARCHAR NOT NULL,
	"apellidoTecnico2" VARCHAR NOT NULL,
	"fechaNacimiento" INTEGER NOT NULL,
	"telefonoTecnico1" VARCHAR NOT NULL,
	"telefonoTecnico2" VARCHAR,
	"telefonoTecnico3" VARCHAR,
	PRIMARY KEY("dni")
);

CREATE TABLE "Asignaciones" (
	"idAsignacion" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"maquinaPrincipal" INTEGER NOT NULL,
	"maquinaSecundaria" INTEGER NOT NULL,
	"dniTecnicoi" INTEGER NOT NULL,
	"idTurno" INTEGER NOT NULL,
	"fechaInicio" INTEGER NOT NULL,
	"fechaFinal" INTEGER NOT NULL,
	PRIMARY KEY("idAsignacion")
);

CREATE TABLE "Maquina" (
	"idMaquina" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"idMarca" INTEGER NOT NULL,
	"modeloMaquina" VARCHAR NOT NULL,
	"noSerie" VARCHAR NOT NULL,
	"idProcesoPlanta" INTEGER NOT NULL,
	"idEstatus" SMALLINT NOT NULL,
	PRIMARY KEY("idMaquina")
);

CREATE TABLE "Turno" (
	"idTurno" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"nombreTurno" VARCHAR NOT NULL,
	"horaInicio" INTEGER NOT NULL,
	"horaFin" INTEGER NOT NULL,
	PRIMARY KEY("idTurno")
);

CREATE TABLE "Estatus" (
	"idEstatus" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"nombreEstatus" VARCHAR(255) NOT NULL,
	PRIMARY KEY("idEstatus")
);

ALTER TABLE "Procesos"
ADD FOREIGN KEY("idDificultad") REFERENCES "Dificultad"("idDificultad")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "ProcesoPlanta"
ADD FOREIGN KEY("idProceso") REFERENCES "Procesos"("idProcesos")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "ProcesoPlanta"
ADD FOREIGN KEY("plantaColor") REFERENCES "Plantas"("colorPlanta")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Maquina"
ADD FOREIGN KEY("idProcesoPlanta") REFERENCES "ProcesoPlanta"("idProcesoPlanta")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Maquina"
ADD FOREIGN KEY("idMarca") REFERENCES "Marca"("idMarca")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Maquina"
ADD FOREIGN KEY("idEstatus") REFERENCES "Estatus"("idEstatus")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Asignaciones"
ADD FOREIGN KEY("maquinaPrincipal") REFERENCES "Maquina"("idMaquina")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Asignaciones"
ADD FOREIGN KEY("maquinaSecundaria") REFERENCES "Maquina"("idMaquina")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Asignaciones"
ADD FOREIGN KEY("dniTecnicoi") REFERENCES "Tecnico"("dni")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Asignaciones"
ADD FOREIGN KEY("idTurno") REFERENCES "Turno"("idTurno")
ON UPDATE NO ACTION ON DELETE NO ACTION;
